    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
        if(p.llena())showMessageDialog(this, "Pila llena");
        else{
            if(txtAutor.getText().isEmpty() || txtISBN.getText().isEmpty() ||
                    txtPrecio.getText().isEmpty() || txtTitle.getText().isEmpty() )
                showMessageDialog(this, "Llene todos los campos");
            else{
                p.meter(new Libro(txtISBN.getText(),
                        txtTitle.getText(),
                        txtAutor.getText(),
                        Float.parseFloat(txtPrecio.getText())));
                
                txtAutor.setText("");
                txtISBN.setText("");
                txtPrecio.setText("");
                txtTitle.setText("");
            }
        }
    }//GEN-LAST:event_btnAddActionPerformed

    private void btnSacarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSacarActionPerformed
        if(p.vacia())showMessageDialog(this, "Pila vacia");
        else p.sacar();
    }//GEN-LAST:event_btnSacarActionPerformed

    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        showMessageDialog(this,p.mostrar());
    }//GEN-LAST:event_btnMostrarActionPerformed

